var helps = {"help": {title: "Show this help", description: "Show help about this cli", usage: "help [method]"}};
var methods = {"help": {checkParams: function(params)
{
	if(params.length > 1)
	{
		return "Too many params";
	}
	if(!helps[params[0]])
	{
		return "Help about this command does not exists";
	}
}, exec: function(params)
{
	return "Description: " + helps[params[0]].description + "\nUsage: " + helps[params[0]].usage;
}}};
var defaultMethod = "help";
var args = process.argv.slice(2);
var method = args[0] || defaultMethod;
if(methods[method])
{
	var error = methods[method].checkParams(args.slice(1));
	if(!error)
	{
		console.log(methods[method].exec(args.slice(1)));
	}
	else
	{
		console.log(error);
		if(helps[args[0]])
		{
			console.log("Usage: " + helps[args[0]].usage);
		}
	}
}
else
{
	console.log("This method does not exists");
}