var clc = require("cli-color");
var lemonUtils = require("lemon_utils");

module.exports = {};
module.exports.log = function(level, text)
{
	var consoleText = text;
	var consoleTime = lemonUtils.fillZeros((new Date()).getHours(), 2) + ":" + lemonUtils.fillZeros((new Date()).getMinutes(), 2) + ":" + lemonUtils.fillZeros((new Date()).getSeconds(), 2);
	if(level == "ERROR")
	{
		consoleText = "[" + consoleTime + "] [ERROR] " + clc["bold"](clc["red"](consoleText));
	}
	if(level == "WARN")
	{
		consoleText = "[" + consoleTime + "] [WARN ] " + clc["bold"](clc["yellow"](consoleText));
	}
	if(level == "INFO")
	{
		consoleText = "[" + consoleTime + "] [INFO ] " + clc["bold"](clc["blue"](consoleText));
	}
	if(level == "DEBUG")
	{
		consoleText = "[" + consoleTime + "] [DEBUG] " + clc["bold"](clc["white"](consoleText));
	}
	if(level == "TRACE")
	{
		consoleText = "[" + consoleTime + "] [TRACE] " + clc["white"](consoleText);
	}
	console.log(consoleText);
};
["ERROR", "WARN", "INFO", "DEBUG", "TRACE"].forEach(function(level)
{
	module.exports[level.toLowerCase()] = function(text)
	{
		module.exports.log(level.toUpperCase(), text)
	};
});