module.exports.fillZeros = function(number, width)
{
	width -= number.toString().length;
	if(width > 0)
	{
		return new Array(width + (/\./.test(number) ? 2 : 1)).join("0") + number;
	}
	return number;
};
module.exports.objectDefaults = function(object, defulats, callback)
{
	for(var property in defulats)
	{
		if(defulats.hasOwnProperty(property))
		{
			if(!object.hasOwnProperty(property))
			{
				if(/^!!!/.test(defulats[property]))
				{
					if(callback)
					{
						callback(defulats[property].substr(3));
					}
					else
					{
						throw new Error(defulats[property].substr(3));
					}
				}
				object[property] = defulats[property];
			}
		}
	}
	return object;
};
module.exports.format = function(template, arg1, arg2, etc)
{
	var result = template;
	[].forEach.call(arguments, function(arg, index)
	{
		if(index != 0)
		{
			result = result.split("%" + (index)).join(arg);
		}
	});
	return result;
};